INSERT INTO fist.news_of_faculty (id, date, date_styles, heading, heading_styles, id_picture, picture_styles, text, text_styles) VALUES (1, '2019-05-07 13:25:51', '', 'Уважаемые родители', null, 57, null, '01 декабря в 12:00 (ауд. 2-05, корпус №2 ПГУТИ: Московское шоссе, 77) состоится собрание родителей студентов 1 курса факультета "Информационные системы и технологии".<br>											<br>Повестка дня:<br><br>1. Встреча с представителями факультета<br>2. Итоги внутрисеместровой аттестации студентов факультета<br>3. Ответы на вопросы', null);
INSERT INTO fist.news_of_faculty (id, date, date_styles, heading, heading_styles, id_picture, picture_styles, text, text_styles) VALUES (23, '2019-05-07 12:26:00', null, 'Demo Day акселератора StartupSamara', null, 101, null, '13 декабря 2018 года в отеле ЛОТТЕ состоялось итоговое мероприятие Demo Day акселератора StartupSamara, где студенты ФИСТ выступили со своими проектами перед инвесторами и бизнесменами Самарской области. <br><br>В рамках этой бесплатной тренинговой программы для проектов на ранней стадии развития студенты ФИСТ на протяжении последнего месяца прошли специально разработанную образовательную программу для проектов, получили консультации экспертов, а также содействие в получении инвестиций и установлении полезных контактов. <br><br>Поздравляем участников с успешным прохождением акселерационной программы и желаем им успеха в разработке своих проектов!<br><br>Подробности - <a href="https://vk.com/fist_psuti?w=wall-50489116_1630">здесь.</a>', null);
INSERT INTO fist.news_of_faculty (id, date, date_styles, heading, heading_styles, id_picture, picture_styles, text, text_styles) VALUES (24, '2019-05-07 11:26:09', null, 'Интенсивный образовательный тренинг «StudDay» для школьников', null, 3, null, '8 декабря во 2 корпусе ПГУТИ (Московское шоссе, 77) состоялся интенсивный образовательный тренинг «StudDay», организованный ФИСТ и студенческим ИТ-клубом. «StudDay» - это уникальная возможность для учащихся 10-11 класоов школ Самарской области провести день в университете и почувствовать себя на один день студентом!<br><br>В рамках мероприятия школьники узнали о студенческих организациях ПГУТИ, прослушали лекции о разумном распределении своего времени, а также о постановке и достижении целей.<br><br>На ярмарке профессий студенты ПГУТИ рассказали обо всех направлениях обучения в университете и ответили на вопросы. Также на ярмарке были представлены клуб робототехники и курсы по 3D-моделированию и программированию на платформе Unity-3D, которые проводятся для всех желающих в студенческом ИТ-клубе.<br><br>Далее школьники сразились друг с другом в интеллектуальном бою и показали свои силы в игре "Что? Где? Когда?".<br><br>После этого по традиции их провели по этажам и классам в формате квеста, где они разгадывали загадки таинственного ПГУТИ и участвовали в различных конкурсах. <br><br>По итогам мероприятия были награждены самые активные сразу в нескольких номинациях: в игре "Что? Где? Когда?", квесте и фотоконкурсе.<br><br><a href="https://www.instagram.com/p/BrNgns_BgyV/">Фотоотчет</a>', null);
INSERT INTO fist.news_of_faculty (id, date, date_styles, heading, heading_styles, id_picture, picture_styles, text, text_styles) VALUES (25, '2019-05-07 11:26:11', null, 'Благотворительная акция, приуроченная к Всемирному дню детей', null, 0, null, 'В 22 ноября 2018г. студенты ФИСТ участвовали в благотворительной акции, приуроченной к Всемирному дню детей. Был проведен благотворительный концерт в коррекционной ГБОУ школе-интернате №115. Программа представления состояла, в первую очередь, из знакомства с детьми для создания дружеское атмосферы. Следом начались танцы и игры с конкурсами, в которых каждый ребенок получил приз и неимоверную долю счастья и позитива. Ключевым номером стало появление настоящего волшебника, который показал каждому неверующему щепотку магии. Фокусы ввели ребят в истинный восторг, также после шоу маг оказался очень востребован детьми, и ему пришлось поделиться секретами волшебства. <br><br>Организацией всего мероприятия занимались студенты второго курса, обучающиеся по направлению подготовки «Реклама и связи с общественностью». Также стоить отметить огромный вклад в проведение данной акции активистов и штаб студенческих отрядов ПГУТИ «Сеть Связи». Покидая школу-интернат, студенты ФИСТ оставили множество подарков от ПГУТИ, и самое главное – оставили детям море счастья и впечатлений.<br><br><a href = "https://www.instagram.com/p/BrDL5YrhQD2/">Фотоотчет</a>', null);
INSERT INTO fist.news_of_faculty (id, date, date_styles, heading, heading_styles, id_picture, picture_styles, text, text_styles) VALUES (26, '2019-05-07 10:26:15', null, 'IT-проект студентов ФИСТ "Система мониторинга атмосферного воздуха" поддержан Администрацией г.о. Самара<br>', null, 0, null, '29 ноября в Департаменте хозяйства и экологии Администрации г.о. Самара (далее Департамент) состоялась встреча заместителя руководителя Департамента - руководителя управления охраны окружающей среды, Христова Андрея Сергеевича, со студентами 4 курса ФИСТ: Кондратьевым Сергеем, Михаленко Юлией, Осановым Владимиром, Щурихиным Андреем.<br><br>В ходе встречи обсуждался вопрос применения системы автоматизированного мониторинга воздушных масс, разрабатываемой студентами совместно с кафедрой «Программного обеспечения и управления в технических системах» (ПОУТС), под руководством зам. декана ФИСТ по научной работе, доцента кафедры ПОУТС, Коняевой Ольги Сергеевны.<br><br>По результатам встречи было принято решение провести в ближайшее время на территории п. Волгарь испытания автоматизированной системы с целью ее дальнейшей эксплуатации. Департамент выразил признательность команде проекта за активную гражданскую позицию и пригласил вступить в ряды внешних общественных экологических инспекторов.<br><br><a href="https://www.instagram.com/p/Bq7bhxehwqy/">Фотоотчет</a>', null);
