INSERT INTO fist.employee (id, characteristic, curator, email, id_department, id_picture_major, id_picture_minor, name, name_department, phone, pin_number, post, qualification_briefly, qualification_detailed) VALUES (1, '<h4>Достижения:</h4>                <p>Лауреат III степени конкурса цифровой экономики, инноваций и инновационных проектов «Новое поколение» 2017/2018 в номинации «Конкурс инновационных разработок и проектов» Международной Академии Связи, Общественного совета при Федеральном Агентстве Связи и Профсоюза работников связи России (2018 г.);<br>Благодарность Департамента информационных технологий и связи Самарской области (2018 г.).</p>                <h4>Образование:</h4>                <p>Поволжский государственный университет телекоммуникаций и информатики, факультет «Телекоммуникаций и радиотехники», год выпуска - 2010.<br>аспирантура: Поволжская государственная академия телекоммуникаций и информатики, специальность 05.12.13 «Системы, сети и устройства телекоммуникаций», научный руководитель –заведующий кафедры информационной безопасности, д.т.н., профессор Карташевский В.Г.</p>                <h4>Диссертация на соискание ученой степени кандидата технических наук</h4>                <p>«Исследование и разработка алгоритмов приема дискретных сообщений при использовании технологии MIMO в каналах с памятью» по специальности 05.12.13 – Системы, сети и устройства телекоммуникаций. Защищена в 2017 году (г. Самара, Поволжский государственный университет телекоммуникаций и информатики).</p>                <h4>Область научных интересов:</h4>                <p>цифровая обработка сигналов для систем связи нового поколения;<br>проектирование и моделирование автоматических и автоматизированных сложных технических систем;<br>конструирование программного обеспечения для сложных технических систем;<br>мониторинг ресурсов компьютерных сетей.</p>                <h4>Данные о повышении квалификации:</h4>                <p>2017, СРТТЦ (г. Самара, РФ), повышение квалификации преподавателей высшего образования по направлениям «Современные информационные технологии» и «Совершенствование преподавательского мастерства»;<br>2017, Всероссийский форум «Россия 2035» (г. Москва, РФ), «Инновационные методы и формы практико-ориентированной работы с учащимися образовательных учреждений»;<br>2018, ООО «Росинфо» и СРТТЦ (г. Самара, РФ), «1С:Предприятие 8. Основные объекты»;<br>2018, ЧОУ ДПО «1С-Образование» (г. Москва, РФ), «Знакомство с платформой 1С:Предприятие 8.3»;<br>2018, ЧОУ ДПО «1С-Образование» (г. Москва, РФ), «Основные механизмы платформы 1С:Предприятие 8.3»;<br>2018, ЧОУ ДПО «1С-Образование» (г. Москва, РФ), «Основы программирования в системе 1С:Предприятие 8.3»;<br>2018, ООО «1С» (г. Москва, РФ), сертификат 1С:Профессионал «на знание основных механизмов платформы 1С:Предприятие 8.3».</p>', '', 'e-mail: o.konyaeva@psuti.ru', 1, 80, 83, 'Коняева Ольга Сергеевна', 'Деканат', 'Тел.: (846) 228-00-13', 2, 'Заместитель декана ФИСТ по научной работе', 'к.т.н., доцент кафедры программного обеспечения и управления в технических системах', 'Кандидат технических наук, доцент кафедры программного обеспечения и управления в технических системах, член Ученого совета ФИСТ.');
